  React.useEffect(() => {

    console.log('====================================');
    console.log('Cambió el estado de zoo');
    console.log('====================================');
  },[zoo])



  return (
    <div>
      <label>Zoo Name:</label>
      <input value={zoo.zooName} onChange={handleInputChange}/>
      <h1>{zoo.zooName}</h1>
    </div>
  );
}
