import React from "react";
// eslint-disable-next-line no-unused-vars
import Animals from "../Animals/Animals";
// eslint-disable-next-line no-unused-vars
import Species from "../Species/Species";
// import styledZoo from "./Zoo.module.css";

export default function Zoo() {
  /* Escribe ac치 tu c칩digo */

  // const zoo = React.useState(second)
  // zoo[0] aca tenemos el estado
  // zoo[1] aca tenemos la funcion para cambiar el estado

  const [zoo, setZoo] = React.useState(
    {
      zooName: "",
      animals: [],
      species: [],
      allAnimals: [],
    }
  )

  // Opcion 1 
  const handleInputChange = (event) => {
    setZoo({
      ...zoo,
      zooName: event.target.value
    })
  }

  // Opcion 2
  // function handleInputChange () {
  // }

  const handleSpecies = (specie) => {
     const filtro = zoo.allAnimals.filter(animal => animal.specie == specie)
      console.log({filtro});
     setZoo({
      ...zoo,
      animals: filtro
    })
  }

  const handleAllSpecies = (event) => {
    alert('holoa2')

  }

  React.useEffect(() => {
    console.log('cargando la data');
    fetch('http://localhost:3001/zoo')
    .then((res) => res.json())
    .then((data) =>
        setZoo({
          ...zoo,
          animals: data.animals,
          species: data.species,
          allAnimals: data.animals,
        })
    )
    .catch((error) => console.log(error));

  },[])



  return (
    <div>
      <label>Zoo Name:</label>
      <input value={zoo.zooName} onChange={handleInputChange}/>
      <h1>{zoo.zooName}</h1>
      <Species 
        species={zoo.species}
        handleSpecies={handleSpecies}
        handleAllSpecies={handleAllSpecies}
      />
      <Animals  animals={zoo.animals}/>
    </div>
  );
}
